// Компонент SVG
interface SVGProps extends React.HTMLProps<HTMLDivElement> {
    name: string;
}

function SVG({ name, ...props }: SVGProps) {
    const Icon = icons[name];
    if (!Icon) {
        return null;
    }

    return (
        <div className="svg-icon" {...props}>
            {Icon}
        </div>
    );
}

export default SVG;
